    private void botonUbicacionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonUbicacionActionPerformed
        //Buscar la ubicación del usuario del ID introducido
        try 
        {
            String operacion = "UBICACION";
            String identificador = textUsuario.getText();
            String ubicacion;
            String numActividades;
            cliente = new Socket(InetAddress.getLocalHost(), 5000);
            entrada = new DataInputStream(cliente.getInputStream());  
            salida = new DataOutputStream(cliente.getOutputStream());
            salida.writeUTF(operacion);
            salida.writeUTF(identificador);
            ubicacion = entrada.readUTF();
            numActividades = entrada.readUTF();
            salida.close();
            entrada.close();
            cliente.close();
            textUbicacion.setText(ubicacion);
            textActividades.setText(numActividades);
        } 
        catch (IOException ex) 
        {
            Logger.getLogger(Cliente.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_botonUbicacionActionPerformed

