    private void botonAforoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonAforoActionPerformed
        //Muestra el aforo actual de cada actividad
        try 
        {
            String operacion = "AFORO";
            String vestuarios;
            String piscinaOlas;
            String piscinaNinios;
            String tumbonas;
            String toboganes;
            String psicinaGrande;
            cliente = new Socket(InetAddress.getLocalHost(), 5000);
            entrada = new DataInputStream(cliente.getInputStream());  
            salida = new DataOutputStream(cliente.getOutputStream());
            salida.writeUTF(operacion);
            vestuarios = entrada.readUTF();
            piscinaOlas = entrada.readUTF();
            piscinaNinios = entrada.readUTF();
            tumbonas = entrada.readUTF();
            toboganes = entrada.readUTF();
            psicinaGrande = entrada.readUTF();
            salida.close();
            entrada.close();
            cliente.close();
            textVestuario.setText(vestuarios);
            textPiscinaOlas.setText(piscinaOlas);
            textPiscinaNiños.setText(piscinaNinios);
            textTumbonas.setText(tumbonas);
            textToboganes.setText(toboganes);
            textPiscinaGrande.setText(psicinaGrande);
        } 
        catch (IOException ex) 
        {
            Logger.getLogger(Cliente.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_botonAforoActionPerformed

